package _03_morning_zombie;

import javax.swing.JOptionPane;

public class Morningzombie {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String noob = JOptionPane.showInputDialog("What is your name?");
		JOptionPane.showMessageDialog(null, "ok " + noob + " I will be telling you what I do in the morning");
		JOptionPane.showMessageDialog(null, "First I wake up and brush my teeth"); 
		JOptionPane.showMessageDialog(null, "Second I eat and go to school"); 
		JOptionPane.showMessageDialog(null, "ok " + noob + " your arrested for asking me what i dont in the morning!"); 
	}

}
